CREATE VIEW public.v_design_hierarchy AS
 SELECT f.facet_id,
    f.short_description AS facet_description,
    f.enabled AS facet_enabled,
    c.canonical_code,
    c.capability_description,
    c.enabled AS canonical_enabled,
    s.session_id,
    s.session_name,
    s.actor_id,
    s.enabled AS session_enabled,
    i.instruction_id,
    i.step_number,
    i.step_description,
    i.enabled AS instruction_enabled,
    ib.branch_id,
    ib.branch_condition,
    ib.enabled AS branch_enabled
   FROM ((((public.facets f
     LEFT JOIN public.canonicals c ON ((c.facet_id = f.facet_id)))
     LEFT JOIN public.sessions s ON ((s.canonical_code = c.canonical_code)))
     LEFT JOIN public.instructions i ON ((i.session_id = s.session_id)))
     LEFT JOIN public.instruction_branches ib ON ((ib.instruction_id = i.instruction_id)))
  ORDER BY f.facet_id, c.canonical_code, s.session_id, i.step_number, ib.branch_priority;


